import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import NoteDetails from "./NoteDetails";
import { serverUrl } from "../config.js";

const Profile = () => {

  const { user, isAuthenticated } = useAuth0();

  const [responseData, setResponseData] = useState(null);
  const {openNote, setOpenNote} = this.state;

  const openFullNote = (id) => {
    this.setState({
      setOpenNote: id
    });
  };

  const fetchData = async () => {
    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
      "email": `${user.email}`
    });

    var requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow'
    };

    try {
      const response = await fetch(`${serverUrl}/user/`, requestOptions);
      const result = await response.json();
      setResponseData(result);
    } catch (error) {
      console.log('error', error);
      setResponseData(null);
    }
  }

  useEffect(() => {
    fetchData();
  }, []);

  if (!isAuthenticated) {
    return <div>Please log in to see your profile information.</div>;
  }

  return responseData ? (
    <div>
      <header>
        <nav>
          <ul>
            <li><a href="/edit_profile">Edit Profile</a></li>
          </ul>
        </nav>
      </header>
      <main>
        <img src={responseData.picture} alt="Profile Picture" />
        <p>Email: {responseData.email}</p>
        {responseData.savedNotes
          ? responseData.savedNotes.map(note => (
            <Card
              className="cards test-card"
              style={{
                width: "20rem",
                minheight: "25px"
              }}
              key={note.id}
              onClick={() => openFullNote(note.id)}
            >
              <Card.Title onClick={() => openFullNote(note.id)}>{note.articleTitle}</Card.Title>
              {/* <Button
                variant="primary"
                style={{
                  backgroundColor: "darkgreen",
                  border: "darkgreen 1px solid",
                }}
                onClick={() => openFullNote(note.id)}
              >
                {openNote !== note.id ? "open Note" : <NoteDetails note={note} className="popupModal" />}
              </Button> */}
              <Card.Footer>
               {setOpenNote !== note.id ? "" : <NoteDetails note={note} className="popupModal" />}
               </Card.Footer>
            </Card>
          ))
          : "No saved saved Articles"}
      </main>
    </div>
  ) : (
    <div>Loading...</div>
  );
};

export default Profile;
