import React, { useState } from 'react';
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import NoteDetails from "./NoteDetails";
import searchUser from '../utils/searchUser';
import Image from './Image.jpg';


const SearchProfile = (props) => {


  console.log("searchProfileProps ", props)

  const [responseData, setResponseData] = useState()
    
  let _userData;
  
  searchUser(props.searchTerm).then((result) => {
    console.log("Results", result)
    _userData = result;
    setResponseData(result);
  })

  // const [openNote, setOpenNote] = useState(null);

  // const openFullNote = (id) => {
  //   setOpenNote(id);
  // };

  

  return (
    <div>
      <main>
        {responseData &&
        <img>src={responseData.picture} alt={Image}</img>}

        {responseData &&
       <p>Email: {responseData.email}</p>}
      
        {responseData && responseData.savedNotes ? (
          responseData.savedNotes.map((note) => (
            <Card
              className="cards test-card"
              style={{
                width: "20rem",
                minheight: "25px",
              }}
              key={note.id}
            >
              <Card.Title>{note.articleTitle}</Card.Title>
              {/* <Button
                variant="primary"
                style={{
                  backgroundColor: "darkgreen",
                  border: "darkgreen 1px solid",
                }}
                onClick={() => openFullNote(note.id)}
              >
                {openNote !== note.id ? (
                  "open Note"
                ) : (
                  <NoteDetails note={note} className="popupModal" />
                )}
              </Button> */}
            </Card>
        
          ))
        ) : (
          "No saved notes"
        )}
        
      
      </main>
    </div>
  );
};

export default SearchProfile;
